card.usageExample:
import 'package:your_app/widgets/common/card.dart';

CNCard(
  header: CardHeader(
    title: CardTitle('Card Title'),
    description: CardDescription('Card description'),
  ),
  content: CardContent(
    child: Text('Card content'),
  ),
)

button.usageExample:
import 'package:your_app/widgets/common/button.dart';

Button(
  variant: ButtonVariant.primary,
  size: ButtonSize.md,
  onPressed: () {
    // Handle button press
  },
  child: Text('Click me'),
)

// With icon
Button(
  variant: ButtonVariant.secondary,
  onPressed: () {},
  icon: Icon(Icons.add),
  iconPosition: IconPosition.leading,
  child: Text('Add Item'),
)

bottom-sheet.usageExample:
import 'package:your_app/widgets/common/bottom_sheet.dart';

showCNBottomSheet(
  context: context,
  builder: (context) => CNBottomSheet(
    title: 'Share',
    description: 'Share this item with others',
    child: Column(
      children: [
        ListTile(title: Text('Share via Email')),
        ListTile(title: Text('Share via SMS')),
      ],
    ),
  ),
)
